# plug-and-play APC scheduler
def make_apc_scheduler(optimizer, warm_lr=2e-4, trigger_eps=0.01, window=50):
    state = dict(step=0, ema=None, triggered=False, k=0, K=None)
    def scheduler(loss):
        state['step'] +=1
        state['ema'] = loss if state['ema'] is None else 0.98*state['ema']+0.02*loss
        if not state['triggered'] and state['step']%window==0:
            prev=state['ema_prev'] if 'ema_prev' in state else state['ema']+1e-8
            if (prev-state['ema'])/prev < trigger_eps:
                state.update(triggered=True, K=int(0.4*(total_steps-state['step'])), k=0)
            state['ema_prev']=state['ema']
        if state['triggered'] and state['k']<=state['K']:
            p=state['k']/state['K']; lr=warm_lr*((1-(p**0.5))+0.1*(p**0.5))
            state['k']+=1
        else:
            lr= warm_lr if not state['triggered'] else warm_lr*0.1
        for g in optimizer.param_groups: g['lr']=lr
    return scheduler

# BEoS PyTorch skeleton
class BEoS:
    def __init__(self, opt, lr0=2e-4, w=32, beta=0.95, rho_min=0.05, rho_max=0.15, gd=0.8):
        self.o,opt=opt,opt; self.w=w; self.c=0; self.beta=beta
        self.rho_min, self.rho_max, self.gd = rho_min, rho_max, gd
        self.lr=lr0; self.lambda_ema=None
    def hutch_trace(self, loss, params):
        v=[torch.randn_like(p) for p in params];
        vs=[(v_i*p).sum() for v_i,p in zip(v,params)]; g=torch.autograd.grad(loss,params,create_graph=True)
        hv=torch.autograd.grad(g, params, vs, retain_graph=True)
        return torch.stack([ (hv_i*v_i).sum() for hv_i,v_i in zip(hv,v)]).sum()
    def step(self, loss):
        self.c+=1
        if self.c%self.w==0:
            lam=self.hutch_trace(loss,[p for g in self.opt.param_groups for p in g['params'] if p.requires_grad])
            self.lambda_ema = lam if self.lambda_ema is None else self.beta*self.lambda_ema+(1-self.beta)*lam
            rho=self.lr*self.lambda_ema.item()
            if rho>self.rho_max: self.lr*=self.gd
            elif rho<self.rho_min: self.lr/=self.gd
            for g in self.opt.param_groups: g['lr']=self.lr
